import { createFeatureSelector, createSelector, createReducer, on } from '@ngrx/store';
import * as fromActions from '../actions/user.actions';
export const initialState = {
  users: []
};

const _userReducer = createReducer(initialState, on(fromActions.DeleteUserAction, (state, {
  payload
}) => {
  const _users = state.users.filter(o => o.id != payload);

  return {
    users: _users
  };
}), on(fromActions.UpdateUserAction, (state, {
  payload
}) => {
  const _users = state.users;

  if (payload[0] == payload[1].id) {
    const _user = _users.find(o => o.id == payload[0]);

    state.users[state.users.indexOf(_user)] = payload[1];
  }

  return {
    users: state.users
  };
}), on(fromActions.LoadAllUserAction, state => {
  return Object.assign(Object.assign({}, state), {
    users: []
  });
}), on(fromActions.LoadUserAction, (state, {
