import { createFeatureSelector, createSelector, createReducer, on } from '@ngrx/store'; //import { Ticket } from 'src/models/suptickets';

import * as fromActions from '../actions/tickets.actions';
export const initialState = {
  tickets: []
};
let tickets = [];

const _ticketReducer = createReducer(initialState, on(fromActions.CreateSupTicketAction, (state, {
  payload
}) => {
  tickets.push(payload);
  return Object.assign(Object.assign({}, state), {
    tickets: [...tickets]
  });
}), on(fromActions.DeleteSupTicketAction, (state, {
  payload
}) => {
  const _tickets = state.tickets.filter(o => o.id != payload);

  return {
    tickets: _tickets
  };
}));

export function ticketReducer(state, action) {
  return _ticketReducer(state, action);
}
